			function saveZjjg(){
				var rows= ui.table("table_zj").getSelectedRows();
				if(rows.length<=0)
				{
					ui.alert("请先选择一条数据");
					return false;
				}
				var editRows = ui.table("table_zj").getEditRowsData(rows);
				for(var k in editRows){
					editRows[k].ljid = ui.hidden("s_ljid").val();
		            editRows[k].gdid = ui.hidden("s_gdid").val();
		            editRows[k].scrwid = ui.hidden("s_scrwid").val();
		            editRows[k].gxzid = ui.hidden("s_gxzid").val();
		            editRows[k].xh = ui.hidden("s_xh").val();
		            editRows[k].jgdyid = ui.hidden("s_jgdyid").val();
		            editRows[k].sfzj = ui.hidden('s_fzj').val();
		            editRows[k].zjbz = ui.hidden('s_jylx').val();
		            editRows[k].zjry = ui.text('zjry').val();
				}

				$.ajax({
		             type: "POST",
		             url:  ui.getRealUrl("save_zjjg"),
		             async: false,
		             data: {data_list : JSON.stringify(editRows)},
		             success: function(data){
		             	ui.table("table_zj").reload();
		             	ui.success("质检保存成功");
		             	ALLReturnIndex();
		             }
		         });
			}
