			function djsmql() {
				$.ajax({
	                type: "POST",
	                url: ui.getRealUrl("/sbgzbjac/djsmql"),
	                async: false,
	                data: {
	                    sbid: ui.hidden("hiddenzsbid").val(),
	                    dw: ui.hidden("zdw").val()
	                },
	                success: function(data) {

	               		$('#sbdw'+sbdjymbz+ui.hidden('zdw').val()).text(0);
	                    ui.success("清零成功", "提示");
	                },
	                error: function(data) {
	                    ui.error("清零失败！");
	                }
	            });
			}



