			function querywlsdByScan() {
			    $("#form-wlsd").attr("action", "queryByWlsd");
			    ui.form("form-wlsd").ajaxSubmit(function() {},
			    function(data) {
			        if (data.returnstatus != null && '' != data.returnstatus.returnstatue) {
			        	ALLCustomreset();
			        	ui.alert(data.returnstatus.returnstatue);
			       	 	ui.hidden("returnstatue").val(data.returnstatus.returnstatue);
			        } else {
			            ui.text("wlbh").val(data.wlbh);
			            ui.text("wlmc").val(data.wlmc);
			            ui.text("jgsl").val(data.jgsl);
			            ui.text("pcbh").val(data.pcbh);
			            ui.text('kssj').val(data.jgkssj);
			            ui.text("sdwz").val(data.sbwz);
			            ui.text("sl").val(data.ysdsl);
			            ui.hidden("scrwbh").val(data.scrwbh);
			            ui.hidden("wllbdm").val(data.wllbdm);
			            ui.hidden("pcmc").val(data.pcmc);
			            ui.hidden("ljmc").val(data.ljmc);
			            ui.hidden("ljbh").val(data.ljbh);
			            ui.hidden("gxid").val(data.gxid);
			            ui.hidden("gxbh").val(data.gxbh);
			            ui.hidden("gdid").val(data.gdid);
			            ui.hidden("gxmc").val(data.gxmc);
			            ui.hidden("jgsl").val(data.jgsl);
			            ui.hidden("jhkssj").val(data.jhkssj);
			            ui.hidden("jhjssj").val(data.jhjssj);
			            ui.hidden("returnnull").val("1");
			            ui.hidden("scrwid").val(data.scrwid);
			            ui.hidden("pcid").val(data.pcid);
			            ui.text('gxzmc').val(data.gxzmc);
			            //ui.text("jgcxmc").val(data.cxmc);
			            ui.text("ybgsl").val(data.gdybgsl);
			            ui.hidden("sbid").val(data.sbid);
			            ui.hidden("gdybgsl").val(data.gdybgsl);
			            ui.hidden("ncbgsl").val(data.ncbgsl);
			            ui.hidden("returnnull").val("1");
			            ui.text("sbbh").val(data.sbbh);

			            ui.plaintext("zljmc").text(data.ljmc);
			            ui.plaintext("zgxzmc").text(data.gxzmc);
			            ui.plaintext("zlckh").text(ui.text("gdtm").val());
			            if('undefined'!=(''+data.gdybgsl))
			            {
			            	ui.plaintext("zwcsl").text(data.gdybgsl+'/'+data.jgsl);
			            }
			            if('undefined'!=(''+data.gxtztpurl1))
			            {
			            	ui.image("gxtztp1").attr("src",data.gxtztpurl1);
			            	ui.bunch('divgxtz1').show();
			            }
			            if('undefined'!=(''+data.gxtztpurl2))
			            {
			            	ui.image("gxtztp2").attr("src",data.gxtztpurl2);
			            	ui.bunch('divgxtz2').show();
			            }
			            if('undefined'!=(''+data.gxtztpurl3))
			            {
			            	ui.image("gxtztp3").attr("src",data.gxtztpurl3);
			            	ui.bunch('divgxtz3').show();
			            }



			        }
			    },
			    function(data) {
			        ui.hidden("returnnull").val("0");
			        ui.alert("查询失败");
			    });

			}
			function saveWlsd() {
				$("#form-wlsd").attr("action", "saveWlsd");
			    var btn = this;
			    gdtm = $('#gdtm').val();
			    sl = $('#sl').val();
			    sbbh = $('#sbbh').val();
			    sdtm = $('#wlsdtm').val();
			    returnnull = $('#returnnull').val();
			    returnstatue = $('#returnstatue').val();
			    if(gdtm == ""){
			    	ui.alert("请输入流程卡号");
			    	return;
			    }
			    if(returnstatue != ''){
	       	 		ui.alert(returnstatue);
	       	 		return;
			    } 
			    //if(sdtm == ""){
			    //	ui.alert("请输入送达位置条码");
			    //	return;
			   // }
			    //if(sbbh != sdtm){
			    //	ui.alert("流程卡信息与送达位置不匹配，请确认后重新输入");
			    //	return;
			   // } 
			    if(Number(sl) <= 0){
			    	ui.alert("送达数量须大于0");
			    	return;
			    }
		        ui.form("form-wlsd").ajaxSubmit(function() {
		            //ui.success("准备提交");
		            //btn.attr("disabled", true);
		        },
		        function(data) {
		            ui.success(data.msg, "提示");
		            ALLReturnIndex();
		        },
		        function(data) {
		            ui.error("保存失败！");
		        });
			}
			function queryGzxx() {
			    $("#form-wlsd").attr("action", "queryGzxx");
			    ui.form("form-wlsd").ajaxSubmit(function() {},
			    function(data) {
			        if (data == null || data == 'null') {
			            ALLCustomreset();
			        } else {
			            ui.text("gzmc").val(data.wlmc);
			            ui.text("gzgg").val(data.wlgg);
			            ui.text("gzsdwz").val(data.gzsdwz);
			            ui.text("gzsdtm").val(data.gzsdtm);
			            ui.text("wllbmc").val(data.wllbmc);
			            ui.hidden("wllbdm").val(data.wllbdm);
			            ui.hidden("returnnull").val("1");
			            ui.hidden("wltm").val(data.wltm);
			            $('#sdtm').focus();
			        }
			    },
			    function(data) {
			        ui.hidden("returnnull").val("0");
			        ui.alert("查询失败");
			    });
			}

			function changesdtm() {
			 $("#form-wlsd").attr("action", "querySdtm");
			    ui.form("form-wlsd").ajaxSubmit(function() {},
			    function(data) {
			    	if (data == null || data == 'null') {
			            ui.hidden("sdtmflag").val("0");
			        } else {
			        	ui.hidden("sdtmflag").val(data.sdtmflag);
			        	ui.text("sbmc").val(data.sbmc);
		            	$('#sdtm').blur();
			        }
			    },
			    function(data) {
			    	ui.hidden("sdtmflag").val("0");
			    });
			}

