		function saveBg() {
			$("#form-wlsd").attr("action", "saveBg");
		    var btn = this;
		    gdtm = $('#gdtm').val();
		    returnnull = $('#returnnull').val();
		    returnstatue = $('#returnstatue').val();
		    var bfgf = Number($('#bfgf').val());
		    var bflf = Number($('#bflf').val())
		    $('#bfsl').val(bfgf + bflf);
		    bgsl = $('#bgsl').val();
		    bfsl = $('#bfsl').val();

		    if(gdtm == ""){
		     	ui.alert("请输入流程卡号");
		     	return;
		    }
		    if(returnstatue != ''){
	       	 		ui.alert(returnstatue);
	       	 		return;
			} 
		    //if(returnnull != 1){
		     	//ui.alert("找不到该流程卡信息");
		     	//return;
		    //}

		    var bgsl = Number($('#bgsl').val());
		    if( bgsl < 0 ){
		    	ui.alert("合格数量不可小于0");
		     	return;
		    }
		    if( /^[+-]?[1-9]?[0-9]*\.[0-9]*$/.test(bgsl)){
		    	ui.alert("合格数量不能为小数");
		     	return;
		    }


		    var bfgf = Number($('#bfgf').val());
		    if( bfgf < 0 ){
		    	ui.alert("工废数量不可小于0");
		     	return;
		    }
		    if( /^[+-]?[1-9]?[0-9]*\.[0-9]*$/.test(bfgf)){
		    	ui.alert("工废数量不能为小数");
		     	return;
		    }
		    var bflf = Number($('#bflf').val())
		    if( bflf < 0 ){
		    	ui.alert("料废数量不可小于0");
		     	return;
		    }
		    if( /^[+-]?[1-9]?[0-9]*\.[0-9]*$/.test(bflf)){
		    	ui.alert("料废数量不能为小数");
		     	return;
		    }

		    if(bgsl == 0 && bfgf == 0 && bflf ==0){
		    	ui.alert("请输入合格数量、工废或者料废数量");
		     	return;
		    }


	        ui.form("form-wlsd").ajaxSubmit(function() {
	        },
	        function(data) {
	            ui.success(data.msg, "提示");
	            ui.text("gdtm").val("");
	            ui.text("bgsl").val("");
	            ui.text("bfsl").val("");
	            ALLCustomreset();
	            $('#gdtm').focus();
	        },
	        function(data) {
	            ui.error("保存失败！");
	        });
		}

