			function querySbxx() {
			    sbtm = $('#sbtm').val();
			    $.ajax({
			        type: "POST",
			        url: ui.getRealUrl("/sbgzbjac/querySbxx"),
			        async: false,
			        data: {
			            sbtm: sbtm
			        },
			        success: function(data) {
			            if (data == null || data == 'null') {
			                ui.hidden("returnnull").val("0");
			                //ui.alert("没有查询出该设备信息");
			            } else {
			                ui.hidden("returnnull").val("1");
			                // $('#sbtm').blur();
			            }
			        },
			        error: function(data) {
			            ui.alert("查询失败");
			        }
			    });
			}
			function saveGzbj() {
			    sbtm = $('#sbtm').val();
			    selectdj = $('#selectdj').val();
			    selectjj = $('#selectjj').val();
			    selectlj = $('#selectlj').val();
			    returnnull = $('#returnnull').val();
			    if(sbtm == ""){
			   	 	ui.alert("请输入设备条码");
			   	 	return;
			    }
			    if(returnnull != 1){
			   	 	ui.alert("找不到该设备信息");
			   	 	return;
			    }
			    if(selectdj == 0 && selectjj == 0 && selectlj == 0){
			   	 	ui.alert("请输入报警内容");
			   	 	return;
			    }
	            $.ajax({
	                type: "POST",
	                url: ui.getRealUrl("/sbgzbjac/saveGzbj"),
	                async: false,
	                data: {
	                    sbtm: sbtm,
	                    selectdj: selectdj,
	                    selectjj: selectjj,
	                    selectlj: selectlj
	                },
	                success: function(data) {
	                    ui.success(data.msg, "提示");
	                    $('#sbtm').focus();
	                },
	                error: function(data) {
	                    ui.error("保存失败！");
	                }
	            });
			}

